const Post = ({ title, summary, cover, createdAt }) => {
  return (
    <div className="post">
      <div className="image">
        <img
          src="https://techcrunch.com/wp-content/uploads/2020/04/GettyImages-909208346.jpg?w=1390&crop=1"
          alt="postImage"
        />
      </div>
      <div className="texts">
        <h2>{title}</h2>
        <p className="info">
          <a href="#" className="author">
            Veysel Kartalmis
          </a>
          <time>{createdAt}</time>
        </p>
        <p className="summary">{summary}</p>
      </div>
    </div>
  );
};

export default Post;
